// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.4;

interface IBank {
	event Deposited(address token, uint256 tokenIdOrAmount, address owner, string reason, address manager);

	event Withdrawn(
		address token,
		uint256 tokenIdOrAmount,
		address receiver,
		string txid,
		address manager,
		bool minted
	);

	event PayThenWithdrawn(
		address receiver,
		address erc721,
		uint256 tokenId,
		address erc20,
		uint256 amount,
		uint256 fee,
		address taker,
		string txid
	);

	struct PayThenWithdrawRequest {
		address receiver;
		address erc721;
		uint256 tokenId;
		address erc20;
		uint256 amount;
		uint256 fee;
		address taker;
		string txid;
		uint256 expiry;
	}

	struct WithdrawERC721Request {
		address receiver;
		address erc721;
		uint256 tokenId;
		bool mint;
		string txid;
		uint256 expiry;
	}

	struct WithdrawERC20Request {
		address receiver;
		address erc20;
		uint256 amount;
		string txid;
		uint256 expiry;
	}

	function withdrawERC721(
		WithdrawERC721Request calldata request,
		uint8 v,
		bytes32 r,
		bytes32 s
	) external returns (bool);

	function withdrawERC20(
		WithdrawERC20Request calldata request,
		uint8 v,
		bytes32 r,
		bytes32 s
	) external returns (bool);

	function managerWithdrawERC20(
		WithdrawERC20Request calldata request,
		uint8 v,
		bytes32 r,
		bytes32 s
	) external returns (bool);

	function depositERC721(
		address erc721,
		uint256 tokenId,
		address depositer,
		address manager,
		string calldata reason
	) external returns (bool);

	function depositMoreERC721(
		address erc721,
		uint256 tokenId,
		address depositer,
		address manager,
		string calldata reason
	) external returns (bool);

	struct DepositERC721Request {
		address erc721;
		uint256 tokenId;
		string reason;
		uint256 nonce;
		uint256 expiry;
	}

	function depositERC721BySig(
		DepositERC721Request calldata request,
		address manager,
		uint8 v,
		bytes32 r,
		bytes32 s
	) external returns (bool);

	function depositERC20(
		address erc20,
		uint256 amount,
		address depositer,
		address manager,
		string calldata reason
	) external payable returns (bool);

	function depositMoreERC20(
		address erc20,
		uint256 amount,
		address depositer,
		address manager,
		string calldata reason
	) external returns (bool);

	struct DepositERC20Request {
		address erc20;
		uint256 amount;
		string reason;
		uint256 nonce;
		uint256 expiry;
	}

	function depositERC20BySig(
		DepositERC20Request calldata request,
		address manager,
		uint8 v,
		bytes32 r,
		bytes32 s
	) external returns (bool);

	function payThenWithdrawERC721(
		PayThenWithdrawRequest calldata request,
		uint8 v,
		bytes32 r,
		bytes32 s
	) external payable returns (bool);
}
